	  String query = "SELECT * FROM items, lists_items WHERE lists_items.list_id = ?";
	  String[] args = new String[]{String.valueOf(list_id)};
	  
	  //execute
	  Cursor c = db.rawQuery(query, args);
	  
	  return c;
